package main

import "fmt"

func main() {
	fmt.Println(maxNumberOfBalloons(""))
}

func maxNumberOfBalloons(text string) int {
    characters := map[rune]int{'b': 0, 'a': 0, 'l': 0, 'o': 0, 'n': 0}

	for _, char := range text {
        if _, exists := characters[char]; exists {
            characters[char]++
        }
    } 

	characters['l'] /= 2
    characters['o'] /= 2

	minCount := characters['b']
    for _, count := range characters {
		if count < minCount {
            minCount = count
        }
    }
	return minCount
}